mapper.select:
SELECT *
FROM company_detail cd JOIN company c ON cd.com_no = c.com_no
WHERE com_info_no = #{comInfoNo}

mapper.update:
UPDATE user
   SET user_pw = #{userPw}

      ,user_upd_date = now()
WHERE user_no = #{userNo}

UPDATE user
   SET user_birth = #{userBirth}
      ,user_phone = #{userPhone}
      ,user_email = #{userEmail}
      ,user_upd_date = now()
WHERE user_no = #{userNo}
