mapper.update:
UPDATE user
   SET user_name = #{user_name}
      ,user_id = #{user_id}
      ,user_pw = #{user_pw}
      ,user_phone = #{user_phone}
      ,user_gender = #{user_gender}
WHERE username = #{user_name}

mapper.select:
SELECT user_id
FROM user
WHERE user_email = #{userEmail} AND user_name = #{userName}

mapper.update:
UPDATE user
SET user_pw = #{userPw}
WHERE user_id = #{userId}

mapper.select:
SELECT * 
FROM user_social
WHERE provider = #{provider} 
AND social_id = #{socialId}

mapper.update:
UPDATE user_social
   SET username = #{username}
      ,provider = #{provider}
      ,social_id = #{socialId}
      ,name = #{name}
      ,email = #{email}
      ,picture = #{picture}
      ,updated_at = #{updatedAt}
WHERE provider = #{provider} 
AND social_id = #{socialId}
